import {Table, Container, Button, Form} from 'react-bootstrap';
import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import web3 from './web3.js'
import ipfs from './ipfs';
import storehash from './storehash';

class Guide extends Component {
    state = {
        title:'',
        cards:{},
    };

    pack = () => {
        let j = {};
        j[this.state.title] = this.state.cards;
        return(Buffer.from(JSON.stringify(j)));
    }

    unpack = (buf) => {
        let json = JSON.parse(buf.toString());
        for(const p in json) {
            this.setState({title: p});
            this.setState({cards: json[p]});
        }
    }

    render() {
        const items = Object.entries(this.state.cards).map(([f, b]) =>
            <Card front={f} back={b}/>
        );
        return (
            <div className="guide">
                <p>{this.state.title}</p>
                {items}
            </div>
        );
    }
}

class Card extends Component {
    render() {
        return (
            <div className="card">
                <div className="card-face">
                    <p>{this.props.front}</p>
                </div>
                <div className="card-face">
                    <p>{this.props.back}</p>
                </div>
            </div>
        );
    }
}

class GuideForm extends Component {
    state = {
        title: '',
        idCards: [['', '']]     //need index for dynamic relocation and such
    };

    setTitle = (event) => {
        this.setState({title: event.target.value});
    };

    createCard = () => {
        this.state.idCards.push(['', '']);
        this.setState({idCards: this.state.idCards});
    }

    genGuide = () => {
        let g = new Guide();
        g.title = this.state.title;
        //g.cards = this.state.cards;
        return g;
    }

    handleChange = (cid, pos) => (event) => {
        this.state.idCards[cid[pos]] = event.target.value;
        console.log(cid + " " + pos);
        this.setState({idCards: this.state.idCards});
    }

    render() {
        const items = this.state.idCards.map((c, i) =>
            <div className="card">
                <div className="card-face">
                    <input
                        type="text"
                        value={i[0]}
                        onChange={this.handleChange(i, 0)}
                     />
                </div>
                <div className="card-face">
                    <input
                        type="text"
                        value={i[1]}
                        onChange={this.handleChange(i, 1)}
                    />
                </div>
            </div>
        );

        return (
            <div className="guide">
                <Form onSubmit={this.genGuide}>
                    <input 
                        type="text"
                        value={this.state.title}
                        onChange={this.setTitle}
                    />
                    <>{items}</>
                    <Button variant="secondary" onClick={this.createCard}>
                        Create Card
                    </Button>
                    <Button variant="primary" type="submit">
                        Create Guide
                    </Button>
                </Form>
            </div>
        );
    };
}

export default class App extends Component {
    state = {
        eguide:<GuideForm/>,
        guide:null,
        ipfsHash:null,
        buffer:'',
        title:'',
        ethAddress:'',
        blockNumber:'',
        transactionHash:'',
        gasUsed:'',
        txReceipt:''
    };

    setTitle = (event) => {
        this.setState({title: event.target.value});
    };

    convertToBuffer = async(reader) => {
        const buffer = await Buffer.from(reader.result);
        this.setState({buffer});
    };

    onClick = async () => {
        try {
            this.setState({blockNumber:"waiting..."});
            this.setState({gasUsed:"waiting..."});
            await web3.eth.getTransactionReceipt(this.state.transactionHash, (txReceipt)  =>{
                console.log(txReceipt);
                this.setState({txReceipt});
                });
            await this.setState({blockNumber: this.state.txReceipt.blockNumber});
            await this.setState({gasUsed: this.statetxReceipt.gasUsed}); 
        } catch (error) {
            console.log(error);
        }
    };

    onSubmit = async (event) => {
        event.preventDefault();
/*        
        //test
        web3.eth.getAccounts(console.log)

        const accounts = await web3.eth.getAccounts()

        console.log('Sending from Metamask account ' + accounts[0]);
        */
        const ethAddress = await storehash.options.address;
        this.setState({ethAddress});

        this.setState({guide:new Guide()});
        this.setState({buffer:this.state.guide.pack()});
        await ipfs.add(this.state.buffer, (err, ipfsHash) => {
            console.log(err,ipfsHash);
            this.setState({ ipfsHash:ipfsHash[0].hash });

            storehash.methods.sendGuide(this.state.title, this.state.ipfsHash).send(
            {from: null}, (error, transactionHash) => {
                console.log(transactionHash);
                this.setState({transactionHash});
            });
        })
    };

    loadGuide = (guide) => {

    }

    render() {
        return (

<div className="App">
    <Container>
        <>{this.state.eguide}</>
        <h3>Upload Guide</h3>
        <Form onSubmit={this.onSubmit}>
            <input
                type = "text"
                value = {this.state.Title} 
                onChange = {this.setTitle}
            />

            <Button variant="primary" type="submit">
                Upload!
            </Button>
        </Form>
        <hr />
        <Button onClick = {this.onClick}>Get Receipt</Button>
        <Table bordered responsive>
            <thead>
                <tr>
                    <th>Tx Receipt Category</th>
                    <th>Values</th>
                </tr>
            </thead>
            <tbody>
                <tr>
                    <td>Link</td>
                    <td>https://gateway.ipfs.io/ipfs/{this.state.ipfsHash}</td>
                </tr>
                <tr>
                    <td>IPFS Hash # on Contract</td>
                    <td>{this.state.ipfsHash}</td>
                </tr>
                <tr>
                    <td>Ethereum Contract Address</td>
                    <td>{this.state.ethAddress}</td>
                </tr>
                <tr>
                    <td>Tx Hash # </td>
                    <td>{this.state.transactionHash}</td>
                </tr>
                <tr>
                    <td>Block #</td>
                    <td>{this.state.blockNumber}</td>
                </tr>
                <tr>
                    <td>Gas Used</td>
                    <td>{this.state.gasUsed}</td>
                </tr>
            </tbody>
        </Table>
        <Guide/>
    </Container>
</div>

        );
    };
}
